import React from "react";
import { Avatar, List, Card } from "antd";
const data = [
  {
    title: "ChatMind",
    openSource: "n",
    href: "https://chatmind.tech/cn?ref=ai-bot.cn",
    input: "用户需求",
    output: "思维导图",
    brief: `
    优势：
1.多模态支持： Chatmind支持多种输入方式，包括文本、语音、图像等，使用户能够更灵活地进行交互和思维导图创作。
2.强大的上下文理解与生成： 由GPT-4驱动的Chatmind具备强大的上下文理解和生成能力，能够更好地理解用户输入并生成更有逻辑和连贯性的回应，有助于创建更智能化的思维导图。
3.实时数据获取： Chatmind能够准确获取实时数据，为用户提供及时、最新的信息，有助于保持思维导图的时效性
    `,
  }
];
const Tools = () => (
  <div>
    <Card
      style={{
        "border-radius": "0px",
        "font-size": "20px",
        "text-align": "center",
      }}
    >
      <p>DesAIgn Tools List</p>
    </Card>
    <List
      itemLayout="horizontal"
      dataSource={data}
      renderItem={(item, index) => (
        <List.Item>
          <List.Item.Meta
            avatar={
              <Avatar
                src={`https://xsgames.co/randomusers/avatar.php?g=pixel&key=${index}`}
              />
            }
            title={<a href="https://ant.design">{item.title}</a>}
            description={item.brief}
          />
        </List.Item>
      )}
    />
  </div>
);
export default Tools;
